    public String encrypt(String str) {
        String str2;
        try {
            str2 = Base64.encodeToString(str.getBytes("UTF-8"), 0);
        } catch (UnsupportedEncodingException e) {
            e.printStackTrace();
            str2 = null;
        }
        return encrypt(str2, "mykey");
    }

    public String decrypt(String str) {
        return decrypt(str, "mykey");
    }

    public String encrypt(String str, String str2) {
        try {
            String str3 = new String(Base64.encode(xor(str.getBytes("UTF-8"), str2), 2));
            String decrypt = "enrypted : ";
            return str3;
        } catch (UnsupportedEncodingException unused) {
            String str4 = new String(Base64.encode(xor(str.getBytes(), str2), 2));
            return str4;
        }
    }

    public String decrypt(String str, String str2) {
        try {
            return new String(xor(Base64.decode(str, 2), str2), "UTF-8");
        } catch (UnsupportedEncodingException unused) {
            return new String(xor(Base64.decode(str, 2), str2));
        }
    }

    public boolean overflow(String str, String str2) {
        return str != null && (str.length() * 4) / 3 >= 65535;
    }

    private static byte[] xor(byte[] bArr, String str) {
        int length = bArr.length;
        int length2 = str.length();
        int i = 0;
        int i2 = 0;
        while (i < length) {
            if (i2 >= length2) {
                i2 = 0;
            }
            bArr[i] = (byte) (bArr[i] ^ str.charAt(i2));
            i++;
            i2++;
        }
        return bArr;
    }

